import React, { useEffect, useState } from "react";
import Header from '../layout/Header'
import Contents from '../layout/Contents'
import Footer from '../layout/Footer'
import Title from '../layout/Title'
import Contact from '../layout/Contact'
import MovieList from "../includes/MovieList";

function Movie() {
  const [movies, setMovies] =useState([]);

  useEffect(()=>{
    const requestOption ={
      method:"GET",
      redirect:"follow",
    };

    fetch("https://api.themoviedb.org/3/search/movie/?api_key=70161310dc890f9fe8ba797d4aa3bcbc&query=Jack+Reacher",
    requestOption
    )
    .then((response) => response.json())
    .then((result) => console.log(result))
    .then((result) => {setMovies(result);})
    .catch((error) => console.log("error", error));
  });
  return (
    <>
      <Header/>
      <Contents>
        <Title title={["movie","reference"]}/>
          <section className='movie__cont'>
            <div className='movie__inner'>
              <MovieList movies={movies}/>
            </div>
          </section>
        <MovieCont/>
        <Contact/>
      </Contents>
      <Footer/>
    </>
  )
}

export default Movie
